package com.example.day4.pojo;

/*
 * POJO stands for Plain Old Java Object
 * it is an ordinary Java Object, not bound by any special restrictions
 * other than those forced by the Java Language
 */

public class POJOExample {
	
	private String name = "POJO Object";
	private int age = 10;
	
	public POJOExample() {
		// TODO Auto-generated constructor stub
	}
	
	public int getAge() {
		return this.age;
	}

}
